export default {
  data: function data() {
    return {
      leaveType: '',
      leaveTypes: ['補休'],
      agent: '',
      agents: ['蔡奇宏'],
      datePicker: {
        date: new Date().toISOString().substr(0, 10),
        menu: false,
        modal: false,
        menu2: false,
        status: true
      },
      timePicker: {
        timeFrom: null,
        timeTo: null,
        menu2: false,
        modal: false,
        modal2: false,
        status: true
      },
      total: {
        value: 0,
        massage: "請選擇時段",
        massageColor: "white",
        status: false
      },
      daySum: 1,
      color: {
        bgc: "#333333",
        but: "#A78E70"
      }
    };
  },
  methods: {
    paseValue: function paseValue() {
      this.$router.push({
        path: '/leave/Submmit',
        query: {
          leaveType: this.leaveType,
          agent: this.agent,
          date: this.datePicker.date,
          timeFrom: this.timePicker.timeFrom,
          timeTo: this.timePicker.timeTo,
          daySum: this.total.value
        }
      });
    },
    timeCaculate: function timeCaculate() {
      var timeFromAry;
      var timeToAry;

      if (this.timePicker.timeFrom && this.timePicker.timeTo) {
        timeFromAry = this.timePicker.timeFrom.split(":");
        timeToAry = this.timePicker.timeTo.split(":");
        var total;

        if (timeToAry[1] < timeFromAry[1]) {
          timeToAry[0] -= 1;
          timeToAry[1] = parseInt(timeToAry[1]);
          timeToAry[1] += 60;
        }

        total = timeToAry[1] - timeFromAry[1];

        if (timeToAry[0] < timeFromAry[0]) {
          this.total.massage = "請輸入正確時間";
          this.total.massageColor = "red";
          this.total.status = false;
        } else {
          total += (timeToAry[0] - timeFromAry[0]) * 60;
          this.total.value = (total / 60).toFixed(2);
          this.total.status = true;
        }
      }
    }
  }
};
