import SearchBar from '@/components/searchBar.vue';
export default {
  components: {
    SearchBar: SearchBar
  },
  props: ['page-title', 'search-placeholder', 'article-list', 'typeList'],
  data: function data() {
    return {
      pageDataNum: 8,
      currentPage: 1,
      totalPage: 1,
      firstPage: '第一頁',
      prev: '上一頁',
      next: '下一頁',
      finalPage: '最末頁',
      theArticleList: '',
      selectedQuery: '',
      searchStatus: false,
      searchQuery: ''
    };
  },
  watch: {
    currentPage: function currentPage(page) {
      this.$router.push({
        query: {
          page: page
        }
      });
      window.scrollTo(0, 0);
    },
    selectedQuery: function selectedQuery(query) {
      this.theArticleList = this.articleList.filter(function (item) {
        return item.type.includes(query);
      });
    },
    searchQuery: function searchQuery(query) {
      var _this = this;

      this.theArticleList = this.articleList.filter(function (item) {
        if (item.title.includes(query)) {
          _this.searchStatus = true;
          return item.title.includes(query);
        }
      });
    },
    theArticleList: function theArticleList() {
      this.pageInit();
    },
    '$route': function $route() {
      this.searchInit();
    }
  },
  beforeMount: function beforeMount() {
    this.searchInit();
    this.pageInit();
    this.selectedQuery = this.$route.params.id;
    if (this.$route.query.page) this.currentPage = this.$route.query.page;
  },
  methods: {
    pageInit: function pageInit() {
      this.totalPage = Math.ceil(this.theArticleList.length / this.pageDataNum);
    },
    setPage: function setPage(page) {
      if (page <= 0 || page > this.totalPage) {
        return;
      } else {
        this.currentPage = page;
      }
    },
    changePath: function changePath(idx) {
      this.$router.push({
        path: this.theArticleList[idx].link
      });
    },
    searchInit: function searchInit() {
      if (this.$route.query.search) this.searchQuery = this.$route.query.search;else this.theArticleList = this.articleList;
    },
    beforeEnter: function beforeEnter(el) {
      el.style.opacity = 0;
      el.style.height = 0;
    },
    enter: function enter(el, done) {
      var delay = el.dataset.index * 150;
      setTimeout(function () {
        Velocity(el, {
          opacity: 1,
          height: '1.6em'
        }, {
          complete: done
        });
      }, delay);
    },
    leave: function leave(el, done) {
      var delay = el.dataset.index * 150;
      setTimeout(function () {
        Velocity(el, {
          opacity: 0,
          height: 0
        }, {
          complete: done
        });
      }, delay);
    }
  }
};
