import SearchBar from '@/components/searchBar.vue';
export default {
  components: {
    SearchBar: SearchBar
  },
  props: ['bookmarkList', 'page-title', 'search-placeholder', 'article-list'],
  data: function data() {
    return {
      pageDataNum: 8,
      currentPage: 1,
      totalPage: 1,
      firstPage: '第一頁',
      prev: '上一頁',
      next: '下一頁',
      finalPage: '最末頁'
    };
  },
  beforeMount: function beforeMount() {
    this.pageInit();
  },
  computed: {// showList: function() {
    //     // return this.bookmarkList.articleList.filter(function ())
    // }
  },
  methods: {
    pageInit: function pageInit() {
      this.totalPage = Math.ceil(this.bookmarkList.articleList.length / this.pageDataNum);
    },
    setPage: function setPage(page) {
      if (page <= 0 || page > this.totalPage) {
        return;
      } else {
        this.currentPage = page;
        window.scrollTo(0, 0);
      }
    },
    changePath: function changePath(idx) {
      this.$router.push({
        path: this.bookmarkList.articleList[idx].link,
        query: {
          articleIndex: idx
        }
      });
    }
  }
};
