import SearchBar from '@/components/searchBar.vue';
export default {
  components: {
    SearchBar: SearchBar
  },
  props: ['article-list', 'page-title', 'search-placeholder', 'type'],
  data: function data() {
    return {
      typeList: ['跨域薈萃', ' 視覺藝術'],
      pageDataNum: 8,
      currentPage: 1,
      totalPage: 1,
      firstPage: '第一頁',
      prev: '上一頁',
      next: '下一頁',
      finalPage: '最末頁',
      theArticleList: '',
      selectedQuery: []
    };
  },
  watch: {
    selectedQuery: function selectedQuery(query) {
      this.theArticleList = this.articleList.filter(function (item) {
        var tempAry;

        for (var q = 0; q < query.length; q++) {
          console.log(query[q]);
          tempAry = item.type.includes(query[q]);
        }

        return tempAry;
      });
    },
    theArticleList: function theArticleList() {
      this.pageInit();
    }
  },
  beforeMount: function beforeMount() {
    this.pageInit();
    this.selectedQuery = this.type;
  },
  methods: {
    pageInit: function pageInit() {
      this.totalPage = Math.ceil(this.theArticleList.length / this.pageDataNum);
    },
    setPage: function setPage(page) {
      if (page <= 0 || page > this.totalPage) {
        return;
      } else {
        this.currentPage = page;
        window.scrollTo(0, 0);
      }
    },
    changePath: function changePath(idx) {
      this.$router.push({
        path: this.theArticleList[idx].link,
        query: {
          articleIndex: idx
        }
      });
    },
    selectedTag: function selectedTag(tag) {
      this.selectedQuery = tag;
    }
  }
};
