#app {
  font-family: 'Microsoft JhengHei', Helvetica, Arial, sans-serif;
}
html {
  text-align: center;
  color: #eee;
}
.home {
  height: 40px;
  width: 100px;
  line-height: 30px;
  padding: 5px;
  border: 1px solid white;
  position: absolute;
  right: 10%;
  bottom: 10%;
  transition: 0.6s;
  cursor: pointer;
}
.home:hover {
  background-color: rgba(255, 255, 255, 0.3);
}
a {
  display: block;
  text-decoration: none;
  font-size: 120%;
}

.page-enter-active, .page-leave-active{
  transition: 0.6s;
}
.page-enter, .page-leave-to{
  opacity: 0;
}
