import ListView from '@/components/ListView.vue'
import newsListinArticle from '@/components/ListViewinArticle'
import { ListArticles } from '@/api/Articles';
export default {
    components: {
        ListView,
        newsListinArticle
    },
    data() {
        return {
            pageTitle: "最新消息",
            type: ["最新消息"],
            typeList: ['最新消息','跨域薈萃',' 視覺藝術'],
            searchPlaceholder: "關鍵字",
            articleList: ''
        }
    },
    beforeMount() {
        this.pageTitle = this.$route.params.id;
    },
    created() {
        this.ApiListArticles(2);
    },
    methods: {
        ApiListArticles(id) {
            ListArticles(id)
                .then(response => {
                    this.articleList = response.data;
                })
                .catch(err => {
                console.log(err);
            });
        }
    }
}
