import SearchBar from '@/components/searchBar.vue'
export default {
    components: {
        SearchBar
    },
    props: ['page-title','search-placeholder','article-list','typeList'],
    data() {
        return {
            pageDataNum: 8,
            currentPage: 1,
            totalPage: 1,
            firstPage: '第一頁',
            prev: '上一頁',
            next: '下一頁',
            finalPage: '最末頁',
            theArticleList: '',
            selectedQuery: '',
            searchStatus: false,
            searchQuery: ''
        }
    },
    watch: {
        currentPage: function(page){
            this.$router.push({query: {page: page} });
            window.scrollTo(0,0);
        },
        selectedQuery: function(query) {
            this.theArticleList = this.articleList.filter((item)=>{
                return item.type.includes(query);
            })
        },
        searchQuery: function(query){
            this.theArticleList = this.articleList.filter((item)=>{
                if(item.title.includes(query)){
                    this.searchStatus = true;
                    return item.title.includes(query);
                }
            })
        },
        theArticleList: function(){
            this.pageInit();
        },
        '$route' (){
            this.searchInit();
        }
    },
    beforeMount(){
        this.searchInit();
        this.pageInit();
        this.selectedQuery = this.$route.params.id;
        if(this.$route.query.page)
            this.currentPage = this.$route.query.page;
    },
    methods: {
        pageInit: function(){
            this.totalPage = Math.ceil(this.theArticleList.length/this.pageDataNum);
        },
        setPage: function(page) {
            if (page <= 0 || page > this.totalPage) {
                return;
            }
            else{
                this.currentPage = page;
            }
        },
        changePath: function(idx){
            this.$route.push({ path: this.theArticleList[idx].link})
        },
        searchInit: function(){
            if(this.$route.query.search)
                this.searchQuery = this.$route.query.search;
            else
                this.theArticleList = this.articleList;
        },
        beforeEnter: function (el) {
            el.style.opacity = 0
            el.style.height = 0
        },
        enter: function (el, done) {
            var delay = el.dataset.index * 150
        setTimeout(function () {
            Velocity(
                el,
                { opacity: 1, height: '1.6em' },
                { complete: done }
                )
            }, delay)
        },
        leave: function (el, done) {
            var delay = el.dataset.index * 150
        setTimeout(function () {
            Velocity(
            el,
            { opacity: 0, height: 0 },
            { complete: done }
            )
        }, delay)
    }
    }
}
