import SearchBar from '@/components/searchBar.vue'
import { ListArticles } from '@/api/Articles';
export default {
    components: {
        SearchBar
    },
    props: ['page-title','search-placeholder','typeList'],
    data() {
        return {
            pageDataNum: 8,
            currentPage: 1,
            totalPage: 1,
            firstPage: '第一頁',
            prev: '上一頁',
            next: '下一頁',
            finalPage: '最末頁',
            theArticleList: '',
            selectedQuery: '',
            searchStatus: false,
            searchQuery: ''
        }
    },
    watch: {
        currentPage: function(page){
            this.$router.push({query: {page: page} });
            window.scrollTo(0,0);
        },
        // selectedQuery: function(query) {
        //     this.theArticleList = this.articleList;
        // },
        searchQuery: function(query){
            // if(query==null)
            //     return
            this.theArticleList = Object.values(this.theArticleList).filter((item)=>{
                if(item.title.includes(query)){
                    this.searchStatus = true;
                    return item.title.includes(query);
                }
            })
        },
        theArticleList: function(){
            this.pageInit();
        },
        '$route' (){
            this.searchInit();
        }
    },
    created() {
        this.ApiListArticles(2);
    },
    mount(){
        this.searchInit();
        this.pageInit();
        // this.selectedQuery = this.$route.params.id;
        // if(this.$route.query.page)
            // this.currentPage = this.$route.query.page;
    },
    methods: {
        ApiListArticles(id) {
            ListArticles(id)
                .then(response => {
                    this.theArticleList= response.data;
                })
                .catch(err => {
                console.log(err);
            });
        },
        stripHTML: function(input) {
            var output = '';
            if(typeof(input)=='string'){
                var output = input.replace(/(<([^>]+)>)/ig,"");
                output = output.replace(/&nbsp;/ig, "");
            }
            return output;
        },
        pageInit: function(){
            this.totalPage = Math.ceil(this.theArticleList.length/this.pageDataNum);
        },
        setPage: function(page) {
            if (page <= 0 || page > this.totalPage) {
                return;
            }
            else{
                this.currentPage = page;
            }
        },
        changePath: function(idx){
            console.log(this.theArticleList[idx].id);
            this.$router.push({ path: this.$route.params.section+'/page/'+this.theArticleList[idx].id})
        },
        searchInit: function(){
            console.log(this.$route.query.search)
            if(this.$route.query.search)
                this.searchQuery = this.$route.query.search;
            else
                this.theArticleList = this.articleList;
        },
        beforeEnter: function (el) {
            el.style.opacity = 0
            el.style.height = 0
        },
        enter: function (el, done) {
            var delay = el.dataset.index * 150
        setTimeout(function () {
            Velocity(
                el,
                { opacity: 1, height: '1.6em' },
                { complete: done }
                )
            }, delay)
        },
        leave: function (el, done) {
            var delay = el.dataset.index * 150
        setTimeout(function () {
            Velocity(
            el,
            { opacity: 0, height: 0 },
            { complete: done }
            )
        }, delay)
    }
    }
}
