h1,p {
    color: #FFF;
   
}

.textArea {
  text-align: left;
  position: relative;
  left: 40%;
}

.circleButton {
  background-color: #A78E70;
  border: none;
  padding: 25px;
  font-size: 30px;
  height: 150px;
  width: 150px;
  margin: 0 auto;
  border-radius: 50%;
  line-height: 100px;
  transition: 0.6s;
  position: relative;
  top: 10%;
  cursor: pointer;
}
.circleButton:hover {
  box-shadow: 2px 2px 2px rgba(51, 51, 102, 0.6), 4px 4px 6px rgba(51, 51, 102, 0.4), 6px 6px 12px rgba(51, 51, 102, 0.4);
}
