import diolog from '@/components/diolog.vue'
import { GetArticle } from '@/api/Articles';
export default {
    props: ['page-title','search-placeholder','articleList'],
    components: {
        diolog
    },
    data() {
        return {
                data: ''
            }
    },
    created() {
        var id = this.$route.params.id;
        this.ApiListArticles(id);
    },
    methods: {
        ApiListArticles(id) {
            GetArticle(id)
                .then(response => {
                    this.data= response.data;
                })
                .catch(err => {
                console.log(err);
            });
        },
    }
}
