export default {
    components: {
    },
    props: ['article-list' ,'page-title'],
    data() {
        return {
            articleIndex: 1,
        }
    },
    methods: {
        changePath: function(idx){
            this.$router.push({ path: this.articleList[idx].link })
        },
        plusPage: function(p){
            this.articleIndex += p;
            var item = document.getElementsByClassName("Listbtn");
            for(var i=p ; i<item.length ; i++){
                if(i==4)
                    break;
                item[i].className += 'active';
            }
        }
    }
}
